TODO: Add this function to the pipelines.kts file.  This auto resolves any breaks, if good data has come in

suspend fun checkMatchStatus() {
  //Check for any auto resolved breaks
  entityDb.getBulk(BREAK_WORKFLOW)
    .toList()
    .filter { it.workflowStatus == WorkflowStatus.Open }
    .forEach {
      val match = entityDb.get(TradesView.byTradeIdVersion(it.tradeId, it.version))
      if (match != null) {
        //Update to latest details and matching status
      it.boTradesCounterparty = match.boTradesCounterparty
      it.foTradesCounterparty = match.foTradesCounterparty
      it.boTradesCurrency = match.boTradesCurrency
      it.foTradesCurrency = match.foTradesCurrency
      it.boTradesNotional = match.boTradesNotional
      it.foTradesNotional = match.foTradesNotional
      it.foTradesReceivedAt = match.foTradesReceivedAt
      it.boTradesReceivedAt = match.boTradesReceivedAt
        if (match.match == true) {
          //Now matches so set to auto resolved
          it.workflowStatus = WorkflowStatus.AutoResolved
        }
        entityDb.audited("pipelineRobot","DataPipeline","Update following new data").modify(it)
      }
    }
}

